mapper.select:
      SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,ref,restep,relevel,parent 
               FROM(    
                    SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,
                    CEIL(rownum/7) AS page,ref,restep,relevel,parent  
                    FROM ( 
                       SELECT gbno,gbcategory,gbtitle,
                       to_char(gbdate,'YYYY.MM.DD') as gbdate,
                       gbhits,id,ref,restep,relevel,parent 
                       FROM ${gbtable}
                       order by ref desc,restep asc  
                        ) 
) WHERE PAGE=#{page}

      SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,ref,restep,relevel,parent 
               FROM(    
                    SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,
                    CEIL(rownum/7) AS page,ref,restep,relevel,parent  
                    FROM ( 
                       SELECT gbno,gbcategory,gbtitle,
                       to_char(gbdate,'YYYY.MM.DD') as gbdate,
                       gbhits,id,ref,restep,relevel,parent 
                       FROM ${gbtable}
                       WHERE   (gbtitle like '%' || #{keyText} || '%' or gbcontent like '%' || #{keyText} || '%') 
                       order by ref desc,restep asc  
                        ) 
) WHERE PAGE=#{page}

      SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,ref,restep,relevel,parent 
               FROM(    
                    SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,
                    CEIL(rownum/7) AS page,ref,restep,relevel,parent  
                    FROM ( 
                       SELECT gbno,gbcategory,gbtitle,
                       to_char(gbdate,'YYYY.MM.DD') as gbdate,
                       gbhits,id,ref,restep,relevel,parent 
                       FROM ${gbtable}
                       WHERE   gbcategory like '%' || #{category} || '%'
                       order by ref desc,restep asc  
                        ) 
) WHERE PAGE=#{page}

      SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,ref,restep,relevel,parent 
               FROM(    
                    SELECT gbno,gbcategory,gbtitle,gbdate,gbhits,id,
                    CEIL(rownum/7) AS page,ref,restep,relevel,parent  
                    FROM ( 
                       SELECT gbno,gbcategory,gbtitle,
                       to_char(gbdate,'YYYY.MM.DD') as gbdate,
                       gbhits,id,ref,restep,relevel,parent 
                       FROM ${gbtable}
                       WHERE   gbcategory like '%' || #{category} || '%' and (gbtitle like '%' || #{keyText} || '%' or gbcontent like '%' || #{keyText} || '%')
                       order by ref desc,restep asc  
                        ) 
) WHERE PAGE=#{page}

select gbno,gbcategory,gbtitle,gbcontent,to_char(gbdate, 'yyyy-mm-dd hh24:mi:ss') as gbdate,gbhits,id,gleader_id, fname, ref,restep,relevel
from ${gbtable} 
where gbno=#{no}

mapper.update:
update ${gbtable}
   set parent= 0
   where ref=#{gbvo.ref}
   AND restep = #{gbvo.restep}+1

UPDATE    ${gbtable} 
    SET       restep = restep+1
   WHERE     ref=#{gbvo.ref}
    AND       restep > #{gbvo.restep}
